custom_css:
/*navbar*/
.shop-btn {
  min-width: 150px !important;
  font-size: 18px;
}

#menu-main-en-mobile {
  margin-top: 30px !important;
}

/*cart counter*/

.fusion-widget-cart-number {
  padding: 3px 3px 2px !important;
  font-size: 16px;
  color: white !important;
  background-color: #deb7ae !important;
}


/*barre info shipping*/
.info-box-container {
    height: 50px !important;
    margin-top: -5px !important;
}

@media screen and (max-width: 640px) {
  .shipping-text {
    font-size: 12px;
  }
}

/*typo*/
.title-about-container h1 {
  color: #3A3331;
}

h3 {
  font-style: normal !important;
}


/*carousel products*/
h4.fusion-carousel-title a, .fusion-carousel-meta a, .woocommerce-loop-category__title, .product-title a {
  color: #3A3331;
  text-align: center !important;
  font-family: 'Montserrat' !important;
  font-size: 20px;
}

h4.fusion-carousel-title a:hover, .fusion-carousel-meta a:hover {
  color: #8A5F5A;
}

h4.fusion-carousel-title, .fusion-carousel-meta {
  text-align: center !important;
}

.btn-categories:hover {
  color: white;
}
.btn-categories {
  color: white;
}

.fusion-body .fusion-carousel .fusion-carousel-nav .fusion-nav-next:before, .fusion-body .fusion-carousel .fusion-carousel-nav .fusion-nav-prev:before {
  background-color: white;
  color: #3A3331 !important;
}




/*fingertips slide*/
.fingertips-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
}

/*carousel services*/
.services-title {
    font-family: 'Monserrat', Arial;
    font-size: 20px;
    font-weight: light !important;
}

.services-btn {
    font-size: 18px;
    font-weight: bold;
    color: white;
    background-color: #b58443;
    padding: 10px 40px;
    margin-top: 40px;
  border-radius: 4px;
}

.services-btn:hover {
    color: white;
    background-color: #D3C4C2;
}

.img-services:active {
  border: none !important;
}
/*service page*/

@media screen and (min-width: 640px) {
  
.colonne-services {
  height: 100% !important;
}
}

.toggle-services .fusion-toggle-heading {
	  color: #8a5f5a;
}

.toggle-services .fa-fusion-box {
	  font-weight: bold;
}

/*product-category page*/

.count {
  display: none;
}

.woocommerce-loop-category__title {
  text-align: center;
}

.term-lashes #main .fusion-row, #slidingbar-area .fusion-row, .term-lashes .fusion-footer-widget-area .fusion-row, .term-lashes .fusion-page-title-row, .term-lashes .tfs-slider .slide-content-container .slide-content {
  max-width: 1200px !important;
}


/*product page*/
.variations .label, .variations .value {
  border: none;
}

.title-sep-container, .product-border.fusion-separator.sep-double.sep-solid, .reset_variations {
  display: none;
}

.woocommerce-product-attributes-item__value {
	  border: none;
}

.ilightbox-title {
	  display: none;
}

@media screen and (min-width: 835px){
button[name="add-to-cart"] {
  margin-left: 20px !important;
}
}

.flex-next, .flex-prev {
 background-color: transparent !important;
 color: #3A3331 !important;
}


/*contact form*/

.wpcf7-submit {
  background-color: #B78E82 !important;
  text-transform: uppercase !important;
}

.wpcf7-submit:hover {
  background-color: #D3C4C2 !important;
}

input[name="your-name"], input[name="email"], input[name="subject"], textarea[name="message"] {
  border: none !important;
  border-bottom: 2px solid #B78E82 !important;
  margin: 10px 0px 0px;
  padding-left: 0px !important;
  background-color: transparent !important;
}

/*slider rev*/
.avada-has-rev-slider-styles .avada-skin-rev {
  border: none;
}

/*wish list page*/

.alg-wc-wl-view-table.shop_table.shop_table_responsive thead {
  display: none !important;
}

td[data-title="Thumbnail"]:before, td[data-title="Title"]:before, td[data-title="Add to cart"]:before, td[data-title="Remove"]:before {
  display: none;
}

td[data-title="Thumbnail"], td[data-title="Title"], td[data-title="Add to cart"], td[data-title="Remove"] {
  display: inline-block !important;
  background-color: transparent !important;

}

@media screen and (max-width: 768px) {
  td[data-title="Title"] {
  font-size: 12px;
  }
}

.alg-wc-wl-view-state.alg-wc-wl-view-state-remove i {
  color: #8a5f5a !important;
}

td[data-title="Add to cart"] p {
  display: inline !important;
}


/*newsletter*/
@media screen and (min-width: 641px) {
	#yikes-mailchimp-container-1 #tommy-1, #yikes-mailchimp-container-2 #tommy-en-2 {
	display: flex;
	flex-direction: row;
}
}


#yikes-easy-mc-form-1-EMAIL, #yikes-easy-mc-form-2-EMAIL {
	padding: 18px;
	color: #333333 !important;
 height: 50px !important;
}

.newsletter__btn {
	background-color: #B58443;
	border: none;
	font-weight: bold;
	color: white;
  height: 50px;
  font-family: 'Montserrat';
  font-size: 18px;
  border-radius: 4px;
}
.newsletter__btn:hover {
	background-color: #D3C4C2;
}

@media screen and (max-width: 768px) {
  .newsletter__btn {
    width: 150px !important;
    margin: auto !important;
  }
}

/*instagram logo*/

.sbi_no_avatar .sbi_header_img {
  background-color: #ffffff;
}

.sbi_new_logo.fa-instagram.fa-w-14 {
  color: #8A5F5A;
}

/*remove categories*/

.product_meta {
  display: none !important;
}

/*privacy bar*/

.fusion-privacy-bar a.fusion-button {
  font-size: 14px !important;
  padding: 8px 20px !important;
}

@media screen and (max-width: 640px) {
  .fusion-privacy-bar .fusion-privacy-bar-main {
    flex-direction: column !important;
    text-align: center !important;
  }
  .fusion-privacy-bar a.fusion-button {
    margin-top: 15px !important;
  }
}
